import { AxiosInstance, AxiosRequestConfig, AxiosResponse, AxiosError } from "axios";
import { CredifyConfig } from "../types";
declare class ApiClient {
    protected readonly instance: AxiosInstance;
    constructor(apiKey: string, accessToken: string, config: CredifyConfig);
    get<T>(url: string, config?: AxiosRequestConfig): Promise<AxiosResponse<T>>;
    post<T>(url: string, body: object, config?: AxiosRequestConfig): Promise<AxiosResponse<T>>;
    formPost<T>(url: string, params: object, config?: AxiosRequestConfig): Promise<AxiosResponse<T>>;
    put<T>(url: string, body: object, config?: AxiosRequestConfig): Promise<AxiosResponse<T>>;
    delete<T>(url: string, config?: AxiosRequestConfig): Promise<AxiosResponse<T>>;
    generateError(url: string, error: AxiosError): Error;
}
export default ApiClient;
