import { Credify } from ".";
export interface FiatCurrency {
    currency: string;
    value: number;
}
export interface Duration {
    value: number;
    unit: string;
}
export interface OrderInfo {
    id: string;
    referenceId: string;
    totalAmount: FiatCurrency | any;
    orderLines: OrderLine[];
    paymentRecipient: PaymentRecipient;
    orderStatus: string;
    bnplAccountNumber: string;
}
export interface OrderLine {
    name: string;
    referenceId: string;
    imageUrl: string;
    productUrl: string;
    quantity: string;
    unitPrice: FiatCurrency | any;
    subtotal: FiatCurrency | any;
    measurementUnit: string;
}
export interface PaymentRecipient {
    name: string;
    number: string;
    branch: string;
    bank: string;
    type: string;
}
export interface BaseResponse {
    success?: boolean;
    data?: any;
}
export interface GetOrderResponse {
    success?: boolean;
    data?: OrderInfo;
}
export interface GetOrdersResponse {
    success?: boolean;
    data?: {
        orders: OrderInfo[];
    };
}
interface KeyPairValue {
    [key: string]: string;
}
export interface GenerateDisbursementDocumentsDataResponse {
    _claim_names: KeyPairValue;
    _claim_sources: {
        [key: string]: {
            JWT: string;
        };
    };
}
export interface CredifyInstance {
    [apiKey: string]: Credify;
}
export interface SimulationInput {
    marketId?: string;
    transactionAmount: FiatCurrency;
    loanTenor: Duration;
    disbursementDate: string;
    downPayment?: FiatCurrency;
    product?: Item;
}
export interface Item {
    manufacturer?: string;
    category?: string;
    name?: string;
}
export {};
